contracts/pipelines/YearnSingleTokenPipe.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "../libraries/Swaps.sol";
import "../libraries/Prices.sol";
import "../interfaces/IPipeline.sol";
import "../interfaces/yearn/IYearnVault.sol";

contract YearnSingleTokenPipeline is IPipeline {
    using Swaps for IRegistry;
    using Prices for IRegistry;

    string public constant PIPELINE_NAME = "YearnSingleTokenPipline";

    // MUTATIVE FUNCTIONS

    function deposit(
        IRegistry registry,
        address vault,
        address tokenIn,
        uint256 amountIn
    ) external override returns (uint256 price) {
        address underlying = IYearnVault(vault).token();

        uint256 supplyAmount;
        if (tokenIn != underlying) {
            supplyAmount = registry.swap(tokenIn, underlying, amountIn);
        } else {
            supplyAmount = amountIn;
        }

        IERC20(underlying).approve(vault, supplyAmount);
        IYearnVault(vault).deposit(supplyAmount);

        price = registry.toUSD(underlying, supplyAmount);
    }

    function withdraw(
        IRegistry registry,
        address vault,
        address tokenOut,
        uint256 shareNum,
        uint256 shareDenom
    ) external override returns (uint256 amountOut) {
        address underlying = IYearnVault(vault).token();

        uint256 withdrawAmount = (IERC20(vault).balanceOf(address(this)) *
            shareNum) / shareDenom;
        withdrawAmount = IYearnVault(vault).withdraw(withdrawAmount);

        if (tokenOut != underlying) {
            amountOut = registry.swap(underlying, tokenOut, withdrawAmount);
        } else {
            amountOut = withdrawAmount;
        }
    }

    // VIEW FUNCTIONS

    function getUnderlying(address vault)
        external
        view
        override
        returns (address[] memory tokens)
    {
        tokens = new address[](1);
        tokens[0] = IYearnVault(vault).token();
    }

    function getPrice(
        IRegistry registry,
        address vault,
        address account
    ) external view override returns (uint256) {
        uint256 totalAssets = IYearnVault(vault).totalAssets();
        uint256 vaultBalance = IYearnVault(vault).balanceOf(account);
        uint256 vaultTotalSupply = IYearnVault(vault).totalSupply();
        if (vaultTotalSupply == 0) {
            return 0;
        }
        uint256 balance = ((totalAssets * vaultBalance) / vaultTotalSupply);
        return registry.toUSD(IYearnVault(vault).token(), balance);
    }
}

contracts/libraries/Swaps.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "../interfaces/IRegistry.sol";

library Swaps {
    function swap(
        IRegistry registry,
        address from,
        address to,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        IRegistry.SwapData memory swapData = registry.getSwapData(from, to);
        if (swapData.swapType == IRegistry.SwapType.UniswapV2) {
            // TODO: Get data and perform uniswap swap
        } else {
            // Perform default swap
            amountOut = defaultUniV2Swap(
                registry.defaultUniswapV2Router(),
                from,
                to,
                amountIn
            );
        }
    }

    function defaultUniV2Swap(
        address router,
        address from,
        address to,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        // Try direct swap first
        address[] memory directPath = new address[](2);
        (directPath[0], directPath[1]) = (from, to);
        uint256 directAmountOut;
        try
            IUniswapV2Router02(router).getAmountsOut(amountIn, directPath)
        returns (uint256[] memory amountsOut) {
            directAmountOut = amountsOut[amountsOut.length - 1];
        } catch {
            // Do nothing
        }

        // Try swap using WETH
        uint256 wethAmountOut;
        address[] memory wethPath = new address[](3);
        address weth = IUniswapV2Router02(router).WETH();
        if (from != weth && to != weth) {
            (wethPath[0], wethPath[1], wethPath[2]) = (from, weth, to);
            try
                IUniswapV2Router02(router).getAmountsOut(amountIn, wethPath)
            returns (uint256[] memory amountsOut) {
                wethAmountOut = amountsOut[amountsOut.length - 1];
            } catch {
                // Do nothing
            }
        }

        // Perform swap
        require(
            directAmountOut > 0 || wethAmountOut > 0,
            "No swap route available"
        );
        amountOut = uniV2Swap(
            router,
            directAmountOut > wethAmountOut ? directPath : wethPath,
            amountIn
        );
    }

    function uniV2Swap(
        address router,
        address[] memory path,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        IERC20(path[0]).approve(router, amountIn);
        uint256[] memory amounts = IUniswapV2Router02(router)
            .swapExactTokensForTokens(
                amountIn,
                0,
                path,
                address(this),
                block.timestamp
            );
        amountOut = amounts[amounts.length - 1];
        require(amountOut > 0, "Can't swap for zero");
    }
}

contracts/libraries/Prices.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "../interfaces/IRegistry.sol";

library Prices {
    using SafeCast for int256;

    uint256 internal constant ONE = 10**8;

    function getPrice(IRegistry registry, address token)
        internal
        view
        returns (uint256)
    {
        AggregatorV3Interface feed = AggregatorV3Interface(
            registry.getPriceFeed(token)
        );
        if (address(feed) != address(0)) {
            (, int256 price, , , ) = feed.latestRoundData();
            if (price < 0) {
                return 0;
            }
            return price.toUint256();
        } else {
            // For now assume all tokens cost 1 USD (i.e. stablecoins)
            return ONE;
        }
    }

    function toUSD(
        IRegistry registry,
        address token,
        uint256 amount
    ) internal view returns (uint256) {
        return
            (amount * getPrice(registry, token)) /
            10**IERC20Metadata(token).decimals();
    }
}

